        const { xmin, ymin, xmax, ymax, step, options } = props;
        const opt = options && options.trim() ? `[${options}]` : "";
        return `\\draw${opt}[step=${step}cm] (${xmin},${ymin}) grid (${xmax},${ymax});\n`;
      }

      case "tikzaxis": {
        const {
          xmin: axmin,
          ymin: aymin,
          xmax: axmax,
          ymax: aymax,
          options,
        } = props;
